import React, { useState, useEffect } from "react";
import axios from "axios";

const Dashboard = () => {
    const [properties, setProperties] = useState([]);
    const [editingProperty, setEditingProperty] = useState(null);
    const [loading, setLoading] = useState(false);
    const [page, setPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);

    // Fetch properties with pagination
    const fetchProperties = async () => {
        setLoading(true);
        try {
            const token = localStorage.getItem("token");
            const { data } = await axios.get(`http://localhost:5000/api/properties?page=${page}&limit=10`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setProperties(data.properties);
            setTotalPages(data.totalPages);
        } catch (err) {
            console.error("Error fetching properties:", err);
            alert("Failed to fetch properties.");
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchProperties();
    }, [page]);

    // Handle delete property
    const handleDelete = async (id) => {
        try {
            const token = localStorage.getItem("token");
            await axios.delete(`http://localhost:5000/api/properties/${id}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            alert("Property deleted successfully!");
            fetchProperties();
        } catch (err) {
            console.error("Error deleting property:", err);
            alert("Failed to delete property.");
        }
    };

    // Handle update property
    const handleUpdate = async (id) => {
        if (!editingProperty.title || !editingProperty.price || isNaN(editingProperty.price)) {
            alert("Please provide valid inputs.");
            return;
        }
        try {
            const token = localStorage.getItem("token");
            await axios.put(`http://localhost:5000/api/properties/${id}`, editingProperty, {
                headers: { Authorization: `Bearer ${token}` },
            });
            alert("Property updated successfully!");
            setEditingProperty(null);
            fetchProperties();
        } catch (err) {
            console.error("Error updating property:", err);
            alert("Failed to update property.");
        }
    };

    // Pagination handlers
    const handleNextPage = () => {
        if (page < totalPages) setPage(page + 1);
    };

    const handlePrevPage = () => {
        if (page > 1) setPage(page - 1);
    };

    return (
        <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
            <button
                onClick={() => (window.location.href = "/add-property")}
                style={{
                    backgroundColor: "#007bff",
                    color: "#fff",
                    padding: "10px 20px",
                    border: "none",
                    borderRadius: "5px",
                    cursor: "pointer",
                    marginBottom: "20px",
                }}
            >
                Add Property
            </button>
            {loading ? (
                <p>Loading properties...</p>
            ) : properties.length === 0 ? (
                <p>No properties listed right now. Click "Add Property" to get started.</p>
            ) : (
                properties.map((property) => (
                    <div
                        key={property._id}
                        style={{
                            border: "1px solid #ccc",
                            padding: "10px",
                            marginBottom: "10px",
                            borderRadius: "5px",
                        }}
                    >
                        {editingProperty && editingProperty._id === property._id ? (
                            <div>
                                <input
                                    type="text"
                                    value={editingProperty.title}
                                    onChange={(e) =>
                                        setEditingProperty({ ...editingProperty, title: e.target.value })
                                    }
                                    style={{ marginBottom: "10px", width: "100%" }}
                                />
                                <textarea
                                    value={editingProperty.description}
                                    onChange={(e) =>
                                        setEditingProperty({ ...editingProperty, description: e.target.value })
                                    }
                                    style={{ marginBottom: "10px", width: "100%" }}
                                ></textarea>
                                <input
                                    type="text"
                                    value={editingProperty.location}
                                    onChange={(e) =>
                                        setEditingProperty({ ...editingProperty, location: e.target.value })
                                    }
                                    style={{ marginBottom: "10px", width: "100%" }}
                                />
                                <input
                                    type="number"
                                    value={editingProperty.price}
                                    onChange={(e) =>
                                        setEditingProperty({ ...editingProperty, price: e.target.value })
                                    }
                                    style={{ marginBottom: "10px", width: "100%" }}
                                />
                                <button
                                    onClick={() => handleUpdate(property._id)}
                                    style={{
                                        backgroundColor: "#28a745",
                                        color: "#fff",
                                        padding: "5px 10px",
                                        border: "none",
                                        borderRadius: "5px",
                                        marginRight: "5px",
                                        cursor: "pointer",
                                    }}
                                >
                                    Save
                                </button>
                                <button
                                    onClick={() => setEditingProperty(null)}
                                    style={{
                                        backgroundColor: "#dc3545",
                                        color: "#fff",
                                        padding: "5px 10px",
                                        border: "none",
                                        borderRadius: "5px",
                                        cursor: "pointer",
                                    }}
                                >
                                    Cancel
                                </button>
                            </div>
                        ) : (
                            <div>
                                <h3>{property.title}</h3>
                                <p>{property.description}</p>
                                <p>{property.location}</p>
                                <p>â‚¹{property.price}</p>
                                <button
                                    onClick={() => setEditingProperty(property)}
                                    style={{
                                        backgroundColor: "#ffc107",
                                        color: "#fff",
                                        padding: "5px 10px",
                                        border: "none",
                                        borderRadius: "5px",
                                        marginRight: "5px",
                                        cursor: "pointer",
                                    }}
                                >
                                    Edit
                                </button>
                                <button
                                    onClick={() => handleDelete(property._id)}
                                    style={{
                                        backgroundColor: "#dc3545",
                                        color: "#fff",
                                        padding: "5px 10px",
                                        border: "none",
                                        borderRadius: "5px",
                                        cursor: "pointer",
                                    }}
                                >
                                    Delete
                                </button>
                            </div>
                        )}
                    </div>
                ))
            )}
            <div style={{ display: "flex", justifyContent: "center", alignItems: "center", marginTop: "20px" }}>
                <button
                    onClick={handlePrevPage}
                    disabled={page === 1}
                    style={{
                        backgroundColor: page === 1 ? "#ccc" : "#007bff",
                        color: "#fff",
                        padding: "5px 10px",
                        border: "none",
                        borderRadius: "5px",
                        cursor: page === 1 ? "not-allowed" : "pointer",
                        marginRight: "10px",
                    }}
                >
                    Previous
                </button>
                <span>
                    Page {page} of {totalPages}
                </span>
                <button
                    onClick={handleNextPage}
                    disabled={page === totalPages}
                    style={{
                        backgroundColor: page === totalPages ? "#ccc" : "#007bff",
                        color: "#fff",
                        padding: "5px 10px",
                        border: "none",
                        borderRadius: "5px",
                        cursor: page === totalPages ? "not-allowed" : "pointer",
                        marginLeft: "10px",
                    }}
                >
                    Next
                </button>
            </div>
        </div>
    );
};

export default Dashboard;
