import React, { useState, useEffect } from "react";
import axios from "axios";

const Dashboard = () => {
    const [properties, setProperties] = useState([]);
    const [editingProperty, setEditingProperty] = useState(null);

    useEffect(() => {
        fetchProperties();
    }, []);

    const fetchProperties = async () => {
        try {
            const token = localStorage.getItem("token");
            const { data } = await axios.get("http://localhost:5000/api/properties", {
                headers: { Authorization: `Bearer ${token}` },
            });
            setProperties(data);
        } catch (err) {
            console.error("Error fetching properties:", err);
        }
    };

    const handleDelete = async (id) => {
        try {
            const token = localStorage.getItem("token");
            await axios.delete(`http://localhost:5000/api/properties/${id}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            alert("Property deleted successfully!");
            fetchProperties();
        } catch (err) {
            console.error("Error deleting property:", err);
            alert("Failed to delete property.");
        }
    };

    const handleUpdate = async (id) => {
        try {
            const token = localStorage.getItem("token");
            await axios.put(`http://localhost:5000/api/properties/${id}`, editingProperty, {
                headers: { Authorization: `Bearer ${token}` },
            });
            alert("Property updated successfully!");
            setEditingProperty(null);
            fetchProperties();
        } catch (err) {
            console.error("Error updating property:", err);
            alert("Failed to update property.");
        }
    };

    return (
        <div>
            <button onClick={() => window.location.href = "/add-property"}>Add Property</button>
            {properties.length === 0 ? (
                <p>No properties listed right now.</p>
            ) : (
                properties.map((property) => (
                    <div key={property._id} style={{ border: "1px solid #ccc", padding: "10px", margin: "10px 0" }}>
                        {editingProperty && editingProperty._id === property._id ? (
                            <div>
                                <input
                                    type="text"
                                    value={editingProperty.title}
                                    onChange={(e) => setEditingProperty({ ...editingProperty, title: e.target.value })}
                                />
                                <textarea
                                    value={editingProperty.description}
                                    onChange={(e) => setEditingProperty({ ...editingProperty, description: e.target.value })}
                                ></textarea>
                                <input
                                    type="text"
                                    value={editingProperty.location}
                                    onChange={(e) => setEditingProperty({ ...editingProperty, location: e.target.value })}
                                />
                                <input
                                    type="number"
                                    value={editingProperty.price}
                                    onChange={(e) => setEditingProperty({ ...editingProperty, price: e.target.value })}
                                />
                                <button onClick={() => handleUpdate(property._id)}>Save</button>
                                <button onClick={() => setEditingProperty(null)}>Cancel</button>
                            </div>
                        ) : (
                            <div>
                                <h3>{property.title}</h3>
                                <p>{property.description}</p>
                                <p>{property.location}</p>
                                <p>${property.price}</p>
                                <button onClick={() => setEditingProperty(property)}>Edit</button>
                                <button onClick={() => handleDelete(property._id)}>Delete</button>
                            </div>
                        )}
                    </div>
                ))
            )}
        </div>
    );
};

export default Dashboard;
